    /**
     * Add limit (and page) to Query Builder.
     *
     * @param QueryBuilder $qb
     * @param int          $limit
     * @param int          $page
     *
     * @return QueryBuilder
     */
    private function addLimitToQueryBuilder($qb, $limit = 0, $page = 0)
    {
        if ($limit > 0) {
            $qb->limit($limit)->skip($limit * $page);
        }

        return $qb;
    }

    /**
     * Add sort to Query Builder.
     *
     * @param QueryBuilder $qb
     * @param array        $sort
     *
     * @return QueryBuilder
     */
    private function addSortToQueryBuilder($qb, $sort = array())
    {
        if (0 !== count($sort)) {
            $qb->sort($sort);
        }

        return $qb;
    }

    /**
     * Add sort and limit (and page) to Query Builder.
     *
     * @param QueryBuilder $qb
     * @param array        $sort
     * @param int          $limit
     * @param int          $page
     *
     * @return QueryBuilder
     */
    private function addSortAndLimitToQueryBuilder($qb, $sort = array(), $limit = 0, $page = 0)
    {
        $qb = $this->addSortToQueryBuilder($qb, $sort);
        $qb = $this->addLimitToQueryBuilder($qb, $limit, $page);

        return $qb;
    }

    public function getGroupsIdsArray($groups)
    {
        // TODO #10479: Find better way to get array with only IDs of groups
        if ($groups) {
            if (gettype($groups) !== 'array') {
                $groups = $groups->toArray();
                $groupsIds = $this->getMongoIds($groups);
            } else {
                $mockString = false;
                $mockGroup = false;
                foreach ($groups as $group) {
                    if (gettype($group) === 'string') {
                        $mockString = true;
                        break;
                    } elseif ($group instanceof Group) {
                        $mockGroup = true;
                        break;
                    }
                }
                if ($mockString) {
                    foreach ($groups as $group) {
                        $groupsIds[] = new \MongoId($group);
                    }
                } elseif ($mockGroup) {
                    $groupsIds = $this->getMongoIds($groups);
                } else {
                    $groupsIds = $groups;
                }
            }
        } else {
            $groupsIds = array();
        }

        return $groupsIds;
    }

    public function findEventsGroupBy()
    {
        $dm = $this->getDocumentManager();
        $collection = $dm->getDocumentCollection('PumukitSchemaBundle:MultimediaObject');

        $pipeline = array();

        $pipeline[] = array(
            '$match' => array(
                'islive' => true,
                'embeddedEvent.display' => true,
                'embeddedEvent.embeddedEventSession' => array('$exists' => true),
            ),
        );

        $pipeline[] = array(
            '$project' => array(
                'multimediaObjectId' => '$_id',
                'event' => '$embeddedEvent',
                'islive' => true,
                'sessions' => '$embeddedEvent.embeddedEventSession',
                'social' => '$embeddedSocial',
                'pics' => '$pics',
                'materials' => '$materials',
                'seriesTitle' => '$seriesTitle',
            ),
        );

        $pipeline[] = array('$unwind' => '$sessions');

        $pipeline[] = array('$match' => array('sessions.start' => array('$exists' => true)));

        $today = strtotime(date('Y-m-d H:i:s', mktime(23, 59, 59, date('m'), date('d'), date('Y'))));

        $pipeline[] = array(
            '$project' => array(
                'multimediaObjectId' => '$multimediaObjectId',
                'event' => '$event',
                'islive' => '$islive',
                'sessions' => '$sessions',
                'social' => '$social',
                'pics' => '$pics',
                'materials' => '$materials',
                'seriesTitle' => '$seriesTitle',
                'session' => '$sessions',
                'groupBy' => array(
                    '$cond' => array(
                        'if' => array(
                            '$and' => array(
                                array(
                                    '$lte' => array(
                                        new \MongoDate(),
                                        array(
                                            '$add' => array(
                                                '$sessions.start',
                                                array(
                                                    '$multiply' => array(
                                                        '$sessions.duration',
                                                        1000,
                                                    ),
                                                ),
                                            ),
                                        ),
                                    ),
                                ),
                                array(
                                    '$gte' => array(
                                        new \MongoDate(),
                                        '$sessions.start',
                                    ),
                                ),
                            ),
                        ),
                        'then' => 'now',
                        'else' => array(
                            '$cond' => array(
                                'if' => array(
                                    '$and' => array(
                                        array(
                                            '$gte' => array(
                                                '$sessions.start',
                                                new \MongoDate(),
                                            ),
                                        ),
                                        array(
                                            '$lte' => array(
                                                '$sessions.start',
                                                new \MongoDate($today),
                                            ),
                                        ),
                                    ),
                                ),
                                'then' => 'today',
                                'else' => array(
                                    '$cond' => array(
                                        'if' => array(
                                            '$lte' => array(
                                                new \MongoDate(),
                                                array(
                                                    '$add' => array(
                                                        '$sessions.start',
                                                        array(
                                                            '$multiply' => array(
                                                                '$sessions.duration',
                                                                1000,
                                                            ),
                                                        ),
                                                    ),
                                                ),
                                            ),
                                        ),
                                        'then' => 'future',
                                        'else' => 'past',
                                    ),
                                ),
                            ),
                        ),
                    ),
                ),
            ),
        );

        $pipeline[] = array(
            '$group' => array(
                '_id' => '$groupBy',
                'data' => array(
                    '$addToSet' => array(
                        'event' => '$event',
                        'multimediaObjectId' => '$multimediaObjectId',
                        'social' => '$social',
                        'pics' => '$pics',
                        'material' => '$material',
                        'session' => '$sessions',
                    ),
                ),
            ),
        );

        return $collection->aggregate($pipeline)->toArray();
    }

